import React, { Component } from 'react'

class TestComponent extends Component {

  state = {
    title: '',
    body: ''
  }
  componentDidMount(){
    console.log(1111);
    fetch('https://jsonplaceholder.typicode.com/posts/1')
          .then(response => response.json())
          .then(data => {
            console.log(data)
            this.setState({
              title: data.title,
              body: data.body,
            });

          })
  }

  // componentDidUpdate(){
  //   console.log(2222);
  // }


  render() {
    const {title, body} = this.state;
    return (
      <div>
        <h2>Posts</h2>

        <div className="posts">
          <div className="card">
            <h3>{title}</h3>
            <p>{body}</p>
          </div>
        </div>

      </div>
    )
  }
}

export default TestComponent;
