import React, { Component } from 'react'
import { Consumer } from "../../context";
// import {v1 as uuid}  from 'uuid';
import TextInputGroup  from "../layout/TextInputGroup";
import axios from "axios";

class EditContact extends Component {
  state = {
    name: '',
    email: '',
    phone: '',
    errors: {}
  }

  onChange = e => this.setState(
    {
      [e.target.name]: e.target.value
    }
  );


  async componentDidMount(){
    const {id} = this.props.match.params;
    console.log(id);
  }

  onSubmit = async (dispatch, e) => {
    e.preventDefault();
    console.log(this.state);

    const {name, email, phone} = this.state;

    if (name === '') {
      this.setState({errors: {name: 'Name must be yours and required!'}});
      return;
    }
    if (email === '') {
      this.setState({errors: {email: 'Email must valid and required!'}});
      return;
    }
    if (phone === '') {
      this.setState({errors: {phone: 'Phone number would not be extra!'}});
      return;
    }

    this.setState({
      name: '',
      email: '',
      phone: '',
      errors: {}
    })

    this.props.history.push('/');

  }


  render() {


    const {name, email, phone, errors} = this.state;



    return (
      <Consumer>
        {value => {
          const { dispatch } = value;
          return(
            <div>
              <div className="card-header h2">
                Edit contact form
              </div>        
              <div className="card card-body mb-3">
                <form onSubmit={this.onSubmit.bind(this, dispatch)}>
                  <TextInputGroup
                    label="Name:"
                    name="name"
                    placeholder="Enter name"
                    onChange={this.onChange}
                    value={name}
                    error={errors.name}
                  />

                  <TextInputGroup
                    label="Email:"
                    name="email"
                    placeholder="Enter email"
                    type="email"
                    onChange={this.onChange}
                    value={email}
                    error={errors.email}
                  />
                  <TextInputGroup
                    label="Phone:"
                    name="phone"
                    placeholder="Enter phone"
                    type="phone"
                    onChange={this.onChange}
                    value={phone}
                    error={errors.phone}
                  />

                  <input type="submit" value="Update Contact" className="btn btn-block btn-primary"/>
                </form>
              </div>
            </div>
          )
        }}
      </Consumer>
    )


  }
}

export default EditContact;
